import React, { useState, useEffect, useRef } from 'react';
import TicTacToeMath from './TicTacToeMath';
import './Game.css'; // You'll need to create this CSS file

function Game() {
  const [game] = useState(new TicTacToeMath());
  const [board, setBoard] = useState(game.board);
  const [currentPlayer, setCurrentPlayer] = useState(game.currentPlayer);
  const [mathQuestion, setMathQuestion] = useState(game.generateMathQuestion());
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');
  const [timeLeft, setTimeLeft] = useState(10);
  const timerRef = useRef(null);

  useEffect(() => {
    startTimer();
    return () => clearInterval(timerRef.current);
  }, [currentPlayer]);

  const startTimer = () => {
    setTimeLeft(10);
    if (timerRef.current) clearInterval(timerRef.current);
    timerRef.current = setInterval(() => {
      setTimeLeft((time) => {
        if (time === 0) {
          clearInterval(timerRef.current);
          switchPlayer();
          return 10;
        }
        return time - 1;
      });
    }, 1000);
  };

  const switchPlayer = () => {
    game.currentPlayer = game.currentPlayer === 'X' ? 'O' : 'X';
    setCurrentPlayer(game.currentPlayer);
    setMathQuestion(game.generateMathQuestion());
    setAnswer('');
    setMessage(`Time's up! ${currentPlayer}'s turn.`);
  };

  const handleCellClick = (index) => {
    if (parseInt(answer) === mathQuestion.answer) {
      if (game.makeMove(index)) {
        setBoard([...game.board]);
        clearInterval(timerRef.current);
        const winner = game.checkWinner();
        if (winner) {
          setMessage(winner === 'tie' ? "It's a tie!" : `Player ${winner} wins!`);
        } else {
          switchPlayer();
        }
      }
    } else {
      setMessage('Incorrect answer. Try again!');
    }
  };

  const handleAnswerChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleAnswerSubmit = (e) => {
    e.preventDefault();
    // The answer is checked in handleCellClick, so we don't need to do anything here
  };

  return (
    <div className="game">
      <h1>Tic Tac Toe Math</h1>
      <div className="game-info">
        <div>Current Player: {currentPlayer}</div>
        <div>Time Left: {timeLeft} seconds</div>
      </div>
      <div className="math-question">
        <p>{mathQuestion.question}</p>
        <form onSubmit={handleAnswerSubmit}>
          <input
            type="number"
            value={answer}
            onChange={handleAnswerChange}
            placeholder="Enter your answer"
          />
          <button type="submit">Submit</button>
        </form>
      </div>
      <div className="game-board">
        {board.map((cell, index) => (
          <button key={index} className="cell" onClick={() => handleCellClick(index)}>
            {cell}
          </button>
        ))}
      </div>
      <div className="message">{message}</div>
    </div>
  );
}

export default Game;
